window.addEventListener("scroll",()=>{document.querySelector("nav").classList.toggle("window-scroll",window.scrollY>100)});window.addEventListener("click",()=>{document.querySelectorAll(".faq").classList.toggle("open")});const allFaqs=document.querySelectorAll(".faq");allFaqs.forEach(n=>{n.addEventListener("click",()=>{n.classList.toggle("open");const t=n.querySelector(".faq__icon i");t.classList.contains("fa-caret-up")?t.classList.replace("fa-caret-up","fa-caret-down"):t.classList.replace("fa-caret-down","fa-caret-up")})});var swiper=new Swiper(".mySwiper",{autoplay:{delay:3e3},slidesPerView:1.5,spaceBetween:30,pagination:{el:".swiper-pagination",clickable:!0},breakpoints:{600:{slidesPerView:2}}});const menu=document.querySelector(".nav__menu"),openMenu=document.querySelector("#open-menu"),closeMenu=document.querySelector("#close-menu");openMenu.addEventListener("click",()=>{menu.style.display="flex",closeMenu.style.display="inline-block",openMenu.style.display="none"});closeMenu.addEventListener("click",()=>{menu.style.display="none",closeMenu.style.display="none",openMenu.style.display="inline-block"});